export default {
  name: "MSnackBar",
  data: function data() {
    return {
      snackbar: false,
      y: 'top',
      x: null,
      mode: '',
      timeout: 2000,
      text: 'Hello, I\'m a snackbar'
    };
  },
  watch: {
    'snackbarVisible': function snackbarVisible() {
      this.snackbar = this.snackbarVisible;
      this.text = this.snackbarText;
    },
    'snackbar': function snackbar() {
      //snackbar set false by timeout
      if (!this.snackbar) this.$store.commit('hideSnackbar');
    }
  },
  computed: {
    snackbarVisible: function snackbarVisible() {
      return this.$store.getters.getSnackbarVisible;
    },
    snackbarText: function snackbarText() {
      return this.$store.getters.getSnackbarText;
    }
  }
};
